import React from 'react';
import iframe from 'react-iframe';

export default function ContactScreen() {

  return (
    <div>
      <h2>Contact Us By Phone</h2>
          <p>
          <a href="callto://+1604111111X"  style={{color: "blue"}}>Call: 604-111-111X</a>
          </p><br></br>

      <h2>Reach Us Via Email</h2>
          <p> 
             <a href="mailto:support@luxeshop.com" style={{color: "blue"}} >Email: support@luxeshop.com </a>
          </p> <br></br>

      <h2>Contact Us Via Live Chat</h2>
          <p>
              Click <a href="" target="_blank"  style={{color: "blue"}}>here </a> to reach a live customer service representative during business hours: Mon-Fri, 9:00am-5:00pm PST (excluding holidays).
         </p><br></br>

      <h2 >Visit Us</h2>

         <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2602.350796767357!2d-123.11341668512063!3d49.28869757933196!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x548671836a6e5787%3A0xd3c5f7bc5d72768d!2sVancouver%20Convention%20Centre!5e0!3m2!1sen!2sca!4v1607900924419!5m2!1sen!2sca" width="600" height="450" frameborder="0" style="border:0;" allowfullscreen="" aria-hidden="false" tabindex="0" ></iframe>

      </div>
  );
}
